$(function() {
  $('.facebook')[0].onclick = function() {
      var newWin;
      newWin = window.open("https://www.facebook.com/sharer/sharer.php?u=" + Window.location, "share", "width=555,height=420,resizable=yes,scrollbars=yes,status=yes");
      return newWin.focus();
    }

  $('.vkontakte')[0].onclick = function() {
      var newWin;
      newWin = window.open("http://vk.com/share.php?url=" + Window.location, "share", "width=650,height=420,resizable=yes,scrollbars=yes,status=yes");
      return newWin.focus();
    }
})
